mapper.update:
update t_cart
set is_deleted = 1
where user_id = #{userId}
and c_id in

    #{item}

update.foreach:
UPDATE t_commodity a
SET inventory = inventory - (
#{item.num}
)
WHERE
a.c_id = #{item.cId}

UPDATE t_commodity a
SET inventory = inventory + (
#{item.num}
)
WHERE
a.c_id = #{item.cId}

mapper.select:
SELECT order_id, sum(sell * a.num) orderAllCost, sum(a.num) orderAllGoodsCount, order_state order_state, a.version FROM ( t_order a LEFT JOIN t_commodity b ON a.c_id = b.c_id ) LEFT JOIN t_user d ON a.user_id = d.user_code WHERE 1=1
and order_state = #{orderState}
and user_id = #{userId} GROUP BY a.user_id, a.order_id, a.order_state, a.shop_id, a.affirm_time, a.version

mapper.update:
update t_order
set order_state = #{orderState},
    version = version + 1
where order_id = #{orderId}
and version = #{version}

update.foreach:
UPDATE t_commodity a
SET num = num + (
#{item.num}
)
WHERE
a.c_id = #{item.cId}
